import React, { useState } from 'react';
import './Matriculas.css';

const Matriculas = () => {
  const [estudiantes, setEstudiantes] = useState([
    { nombre: 'Juan Pérez', correo: 'juan.perez@email.com', carrera: 'Ingeniería de Sistemas' },
    { nombre: 'Ana López', correo: 'ana.lopez@email.com', carrera: 'Medicina' },
    { nombre: 'Carlos Ruiz', correo: 'carlos.ruiz@email.com', carrera: 'Derecho' },
  ]);
  const [nuevoEstudiante, setNuevoEstudiante] = useState({
    nombre: '',
    correo: '',
    carrera: ''
  });

  const handleChange = (e) => {
    setNuevoEstudiante({
      ...nuevoEstudiante,
      [e.target.name]: e.target.value
    });
  };

  const handleAgregar = (e) => {
    e.preventDefault();
    if (
      nuevoEstudiante.nombre.trim() &&
      nuevoEstudiante.correo.trim() &&
      nuevoEstudiante.carrera.trim()
    ) {
      setEstudiantes([...estudiantes, nuevoEstudiante]);
      setNuevoEstudiante({ nombre: '', correo: '', carrera: '' });
    }
  };

  const handleEliminar = (idx) => {
    setEstudiantes(estudiantes.filter((_, i) => i !== idx));
  };

  return (
    <div className="matriculas-container">
      <h2>Estudiantes Registrados</h2>
      <form className="matriculas-form" onSubmit={handleAgregar}>
        <input
          type="text"
          name="nombre"
          placeholder="Nombre"
          value={nuevoEstudiante.nombre}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="correo"
          placeholder="Correo"
          value={nuevoEstudiante.correo}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="carrera"
          placeholder="Carrera"
          value={nuevoEstudiante.carrera}
          onChange={handleChange}
          required
        />
        <button type="submit">Agregar</button>
      </form>
      <table className="matriculas-table">
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Correo</th>
            <th>Carrera</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {estudiantes.map((est, idx) => (
            <tr key={idx}>
              <td>{est.nombre}</td>
              <td>{est.correo}</td>
              <td>{est.carrera}</td>
              <td>
                <button onClick={() => handleEliminar(idx)}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Matriculas;
