import React, { useState, useContext } from "react";
import { EstudiantesContext } from "../context/EstudiantesContext";
import Formulario from "../components/Formulario";
import ListaEstudiantes from "../components/ListaEstudiantes";
import "./Home.css";
import escudo from "../assets/Escudo_Universidad_Autónoma_Tomás_Frías.png";

export default function Home() {
  const { agregarEstudiante } = useContext(EstudiantesContext);
  const [nuevoEstudiante, setNuevoEstudiante] = useState({
    nombre: "",
    correo: "",
    carrera: "",
  });

  const handleChange = (e) => {
    setNuevoEstudiante({
      ...nuevoEstudiante,
      [e.target.name]: e.target.value,
    });
  };

  const handleAgregar = (e) => {
    e.preventDefault();
    if (
      nuevoEstudiante.nombre.trim() &&
      nuevoEstudiante.correo.trim() &&
      nuevoEstudiante.carrera.trim()
    ) {
      agregarEstudiante(nuevoEstudiante);
      setNuevoEstudiante({ nombre: "", correo: "", carrera: "" });
    }
  };

  return (
    <section className="home-section">
      <div className="bienvenida">
        <h2>PRINCIPAL</h2>
      </div>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          margin: "2rem 0",
        }}
      >
        <img
          src={escudo}
          alt="Escudo UATF"
          style={{ maxWidth: 350, width: "100%" }}
        />
      </div>
      <div className="contenido-home">
        <div className="matriculas-container">
          <h2>Registrar Estudiante</h2>
          <form
            className="matriculas-form"
            onSubmit={handleAgregar}
          >
            <input
              type="text"
              name="nombre"
              placeholder="Nombre"
              value={nuevoEstudiante.nombre}
              onChange={handleChange}
              required
            />
            <input
              type="email"
              name="correo"
              placeholder="Correo"
              value={nuevoEstudiante.correo}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="carrera"
              placeholder="Carrera"
              value={nuevoEstudiante.carrera}
              onChange={handleChange}
              required
            />
            <button type="submit">Agregar</button>
          </form>
        </div>
        <Formulario />
        <ListaEstudiantes />
      </div>
    </section>
  );
}
